mapper.select:
select notice_id, notice_title, notice_date, notice_contents, admin_id
from notice
where notice_id = #{notice_id}

mapper.update:
update notice
set notice_title = #{notice_title},
    notice_contents = #{notice_contents}
where notice_id = #{notice_id}

mapper.select:
select admin_id, admin_pw
from admin_table 
where 
	admin_id = #{admin_id} 
	and 
	admin_pw = #{admin_pw}
