mapper.select:
SELECT COUNT(MEMBER_ID)
FROM ALARM 
WHERE MEMBER_ID = #{member_id} AND COMPANY_ID = #{company_id} AND ALARM_MESSAGE = #{alarm_message}

SELECT EMPLOYMENT_ID, EMPLOYMENT_TITLE, EMPLOYMENT_TIME, COMPANY_ID
FROM EMPLOYMENTS
WHERE COMPANY_ID = #{company_id }

SELECT BUSINESS_TYPE, COMPANY_ADDR, BUSINESS_CATEGORY 
FROM COMPANY 
WHERE COMPANY_ID = #{company_id }
