mapper.insert:
insert into resume 		
			(resume_id, 		
			member_id, 		
			resume_title, 		
			hope_job, 		
			hope_salary

			,hope_location	


			,final_education


			,select_resume 		


			,select_public 		


			,resume_coment	

			)
values
			(RESUME_SEQ.nextval,
			#{member_id}, 
			#{resume_title}, 			
			#{hope_job}, 		
			#{hope_salary} 

			,#{hope_location}


			,#{final_education} 


			,#{select_resume} 


			,#{select_public}


			,#{resume_coment}

			)

mapper.update:
update resume
set 

	resume_title = #{resume_title}


	,hope_job = #{hope_job}


	,hope_salary = #{hope_salary}


	,hope_location = #{hope_location}


	,final_education = #{final_education}


	,select_resume = #{select_resume}


	,select_public = #{select_public}


	,resume_coment = #{resume_coment}

where resume_id = #{resume_id}
